"use strict";
        var default_language = "espanol";
        var peer;
        var focusedAudio = "";
        function mv(pages) {
            const myUrl = new URL(window.location.href);
            const curPage = parseInt(myUrl.searchParams.get('page') || "1");
            myUrl.searchParams.set('page', curPage + pages);
            window.history.pushState({}, '', myUrl.href);
            document.getElementById("page").src = "";
            window.scrollTo(0, 0);
            updatePage();
        }
        function softNavigate(event, props) {
            event.preventDefault();
            const myUrl = new URL(window.location.href);
            for (let [key, value] of Object.entries(props)) {
                myUrl.searchParams.set(key, value);
            }
            window.history.pushState({}, '', myUrl.href);
            document.getElementById("page").src = "";
            window.scrollTo(0, 0);
            updatePage();
        }
        function toggleUrlProp(propName) {
            const myUrl = new URL(window.location.href);
            const prop = myUrl.searchParams.has(propName);
            if (prop) {
                myUrl.searchParams.delete(propName);
            } else {
                myUrl.searchParams.append(propName, true);
            }
            window.history.pushState({}, '', myUrl.href);
            return !prop;
        }
        function getParams() {
            var myUrl = new URL(window.location.href);
            var params = myUrl.searchParams;
            var lan = params.get("language") || default_language;
            var page = params.get("page") || "1";
            var level = params.get("level") || "basico";
            var enableaudio = params.has('enableaudio');
            var cursor = params.has('cursor');
            return { lan, page, level, enableaudio, cursor };
        }
        function showHide(id) {
            const ele = document.getElementById(id);
            if (ele.classList.contains("hide")) {
                ele.classList.remove("hide");
                ele.scrollIntoView();
            } else {
                ele.classList.add("hide");
            }

        }
        function posToChapterNumber(language, level, page) {
            var chapters = toc2[`${language}-${level}`];
            if (!chapters) {
                return null;
            }
            var pagesLeft = page - chapters.start_page;
            if (pagesLeft < 0) {
                return null;
            }

            const ppChapter = language === 'chino' || language === 'japones' ? 8 : 6;

            if (pagesLeft < 10*ppChapter) {
                return chapters.start_chapter + Math.floor(pagesLeft / ppChapter);
            }
            pagesLeft -= 10*ppChapter + (level === "superior" ? 2 : 4);
            if (pagesLeft < 10*ppChapter) {
                return chapters.start_chapter + 10 + Math.floor(pagesLeft / ppChapter);
            }
            pagesLeft -= 10*ppChapter + 2;
            if (pagesLeft < 10*ppChapter) {
                return chapters.start_chapter + 20 + Math.floor(pagesLeft / ppChapter);
            }
            return null;
        }
        function rewind(audio) {
            if (audio.currentTime > 5) {
                audio.currentTime -= 5
            } else {
                audio.currentTime = 0;
            }
        }
        function extractAudioNumber(link) {
            const res = /pageConfig\/(\d)/.exec(link);
            if (res && res.length) {
                return res[1];
            }

            const fragments = {
                'apr': 1,
                'rec': 2,
                'his': 4,
                'anto': 1,
                'confer': 2,
                'dont': 4,
                'do': 3,

            };
            for (const frag in fragments) {
                if (link.toLowerCase().indexOf(frag) !== -1) {
                    return fragments[frag];
                }
            }
            return 1;
        }

        function addAudio(link) {
            var audios = document.getElementById('audios-tbody');
            var audio = document.createElement('audio');
            audio.controls = true;
            audio.src = link;
            var tr = document.createElement('tr');
            var td1 = document.createElement('td');
            var audioNum = extractAudioNumber(link);
            console.log({ link, audioNum })
            td1.innerText = `Part ${audioNum}`;
            audio.id = `audio-${audioNum}`;
            tr.appendChild(td1);
            var td2 = document.createElement('td');
            td2.appendChild(audio);
            tr.appendChild(td2);
            var backButton = document.createElement("button");
            backButton.innerText = "\u27F2";
            backButton.style = "font-size: 1em;"
            backButton.onclick = () => {
                rewind(audio);
            }
            var td3 = document.createElement('td');
            td3.appendChild(backButton);
            tr.appendChild(td3);
            audios.appendChild(tr);
        }
        function createAudios() {
            var audios = document.getElementById('audios-tbody').innerHTML = "";
            var { lan, page, level } = getParams();
            var audios = toc2[`${lan}-${level}`].audios[page];
            if (audios) {
                audios.forEach(src => {
                    addAudio(`https://htlescueladeidiomas.com/flipbookhtl/${lan}/${level}/libro/${src}`);
                });
            } else {
                document.getElementById('audios-tbody').innerHTML = "<td><tr>No audios</tr></td>";
            }
        }
        function toggleAudioPlay(id) {
            if (!getParams().enableaudio) {
                return;
            }
            let ele = document.getElementById(id);
            return !!ele && ele.paused ? ele.play() : ele.pause();
        }
        function imgClick() {
            if (!('ontouchstart' in window)) {
                return;
            }
            var audios = document.querySelectorAll('[id^=audio-]')
            if (audios.length === 0) {
                return;
            }
            toggleAudioPlay(audios[0].id);
        }
        function createToc(language) {
            var tocBasico = document.getElementById('toc-basico');
            var tocIntermedio = document.getElementById('toc-intermedio');
            var tocAvanzado = document.getElementById('toc-avanzado');
            var tocSuperior = document.getElementById('toc-superior');

            tocBasico.innerHTML = "";
            tocIntermedio.innerHTML = "";
            tocAvanzado.innerHTML = "";
            tocSuperior.innerHTML = "";

            tocSuperior.parentElement.style.display = language === "ingles" ? "block" : "none";

            for (var i = 0; i < 120; i++) {
                if (i >= 90 && language !== 'ingles') {
                    break;
                }

                var l = i < 30 ? 'basico' : i < 60 ? 'intermedio' : i < 90 ? 'avanzado' : 'superior';
                var chapters = toc2[`${language}-${l}`];
                const ppChapter = language === 'chino' || language === 'japones' ? 8 : 6;

                if (!chapters) {
                    var li = document.createElement('li');
                    li.textContent = `Chapter ${i + 1} not found`;
                } else {
                    var relativeI = i - (chapters.start_chapter - 1);
                    var page =
                        chapters.start_page +
                        relativeI * ppChapter +
                        (relativeI >= 10 ? (l === 'superior' ? 2 : 4) : 0) +
                        (relativeI >= 20 ? 2 : 0);
                    var li = document.createElement('li');
                    const mkLink = (level, page, text) => `<a href="#" title="${l} p. ${page}" onclick="softNavigate(event, {level:'${level}',page:'${page}'})">${text}</a>`;
                    li.innerHTML = `${mkLink(l, page, `chapter ${i + 1}`)} ${mkLink(l, page + 1, "A")} ${mkLink(l, page + 3, "B")} ${chapterNames[language] ? chapterNames[language][i].toLowerCase() : ''}`
                }

                if (l === 'basico') {
                    tocBasico.appendChild(li);
                } else if (l === 'intermedio') {
                    tocIntermedio.appendChild(li);
                } else if (l === 'avanzado') {
                    tocAvanzado.appendChild(li);
                } else if (l === 'superior') {
                    tocSuperior.appendChild(li);
                }
            }
        }
        function getConnections() {
            return JSON.parse(localStorage.getItem('htl-connections') || "{}");
        }
        function setConnections(connections) {
            connections = Object.fromEntries(Object.entries(connections).filter(([key, value]) => new Date(value) > new Date()));
            localStorage.setItem('htl-connections', JSON.stringify(connections));
        }
        function sendMessage(id) {
            var conn = peer.connect(id)
            conn.on("open", function () {
                var { lan, page, level } = getParams();
                conn.send({
                    title: document.title,
                    espanol: `https://htlescueladeidiomas.com/flipbookhtl/espanol/${level}/libro/files/mobile/${page}.jpg`,
                    ingles: `https://htlescueladeidiomas.com/flipbookhtl/ingles/${level}/libro/files/mobile/${page}.jpg`,
                    frances: `https://htlescueladeidiomas.com/flipbookhtl/frances/${level}/libro/files/mobile/${page}.jpg`,
                    italiano: `https://htlescueladeidiomas.com/flipbookhtl/italiano/${level}/libro/files/mobile/${page}.jpg`,
                    portugues: `https://htlescueladeidiomas.com/flipbookhtl/portugues/${level}/libro/files/mobile/${page}.jpg`,
                    aleman: `https://htlescueladeidiomas.com/flipbookhtl/aleman/${level}/libro/files/mobile/${page}.jpg`,
                    ruso: `https://htlescueladeidiomas.com/flipbookhtl/ruso/${level}/libro/files/mobile/${page}.jpg`,
                    japones: `https://htlescueladeidiomas.com/flipbookhtl/japones/${level}/libro/files/mobile/${page}.jpg`,
                    chino: `https://htlescueladeidiomas.com/flipbookhtl/chino/${level}/libro/files/mobile/${page}.jpg`,
                    defaultLanguage: lan
                });
            })
        }
        function updateCursor() {
            var { cursor } = getParams();
            if (cursor) {
                document.getElementById("page").style.cursor = `url('data:image/svg+xml;utf8,<svg fill="%23FF0000" height="48" viewBox="0 0 24 24" width="48" xmlns="http://www.w3.org/2000/svg"><path d="M0 0h24v24H0z" fill="none"/><path d="M7.293 4.707 14.586 12l-7.293 7.293 1.414 1.414L17.414 12 8.707 3.293 7.293 4.707z"/></svg>') 24 24, auto`;
            } else {
                document.getElementById("page").style.cursor = "auto";
            }
        }
        function updatePage() {
            var { lan, page, level, enableaudio } = getParams();
            var chapterNumber = posToChapterNumber(lan, level, page);
            var chapterName = chapterNumber >= 1 && (chapterNumber <= 90 || (lan == "ingles" && chapterNumber <= 120)) && chapterNames[lan] ? chapterNames[lan][chapterNumber - 1] : null;
            console.log({ lan, page, level, chapterNumber, chapterName });
            document.getElementById("page").src = `https://htlescueladeidiomas.com/flipbookhtl/${lan}/${level}/libro/files/mobile/${page}.jpg`;

            document.getElementById("toggle-enableaudio").innerText = enableaudio ? "Disable audio shorcuts" : "Enable audio shorcuts";

            updateCursor();
            createAudios();

            var audios = document.querySelectorAll('[id^=audio-]')
            focusedAudio = audios.length === 0 ? "" : focusedAudio = audios[0].id;

            createToc(lan);

            if (chapterNumber && chapterName) {
                document.title = `${chapterNumber} - ${chapterName}`;
            } else {
                document.title = `${lan} ${level} ${page}`;
            }

            var id = localStorage.getItem('htl-id');
            if (!id) {
                id = crypto.randomUUID();
                localStorage.setItem('htl-id', id);
            }
            console.log({ id })

            var qrCodeEle = document.getElementById("qr-code");
            qrCodeEle.innerHTML = "";
            var qrCode = new QRCode(qrCodeEle, {
                text: `https://htl-qr-code.s3.us-east-1.amazonaws.com/external.html?hostId=${id}`,
                width: 256,
                height: 256,
                colorDark: "#000000",
                colorLight: "#ffffff",
                correctLevel: QRCode.CorrectLevel.H
            });

            if (!!peer) {
                peer.destroy();
                peer = null;
            }

            peer = new Peer(id, { reliable: true });

            peer.on('connection', (conn) => {
                console.log(conn.peer);
                var cons = getConnections();
                cons[conn.peer] = new Date(new Date().getTime() + 4 * 60 * 60 * 1000)
                setConnections(cons);
                conn.on('open', () => {
                    console.log(`open ${conn.peer}`)
                    sendMessage(conn.peer);
                    // conn.send(document.getElementById("page").src);
                });
            });

            peer.on('open', () => {
                console.log("open peer");
                for (let [key, value] of Object.entries(getConnections())) {
                    console.log(`consider ${key} ${value} ${new Date(value) > new Date()}`)
                    if (new Date(value) > new Date()) {
                        console.log(`send ${key}`)
                        sendMessage(key);
                    }
                }
            });

            // Preload the next image
            var img = new Image();
            img.src = `https://htlescueladeidiomas.com/flipbookhtl/${lan}/${level}/libro/files/mobile/${parseInt(page) + 1}.jpg`;
        }
        window.onload = function onLoad() {
            updatePage();
        }
        window.addEventListener('popstate', function(event) {
            updatePage();
        })
        document.onkeydown = function (evt) {
            evt = evt || window.event;
            if (!evt.ctrlKey && evt.key === 'f') {
                if (!document.fullscreen) {
                    document.documentElement.requestFullscreen(Element.ALLOW_KEYBOARD_INPUT);
                } else {
                    document.exitFullscreen();
                }
            } else if (!evt.ctrlKey && evt.key == 'h') {
                toggleUrlProp("cursor");
                updateCursor();
            } else if (!evt.ctrlKey && evt.key == 'm') {
                mv(1);
            } else if (!evt.ctrlKey && evt.key == 'n') {
                mv(-1);
            } else if (!evt.ctrlKey && ['1', '2', '3', '4', '5'].includes(evt.key)) {
                focusedAudio = `audio-${evt.key}`;
                console.log({ focusedAudio, key: evt.key });
            } else if (!evt.ctrlKey && evt.key == ' ') {
                evt.preventDefault();
                toggleAudioPlay(focusedAudio);
            } else if (!evt.ctrlKey && evt.key === ',') {
                rewind(document.getElementById(focusedAudio));
            }
        };

